import React from "react";
import { useNavigate } from "react-router-dom";
import { Button } from "react-bootstrap";
import "./Home.css"; // use home css for one class top section
import Form from "react-bootstrap/Form";
import { useEffect, useState } from "react";
import axios from "axios";

// return to authentication page and erase local storage

const go_to_authentication = (navigate) => {
  let path = `/Authentication`;
  navigate(path);
  localStorage.removeItem("user");
  localStorage.removeItem("user_id");
};

// go to home page
const go_to_home = (navigate) => {
  let path = `/Home`;
  navigate(path);
};

// current user building
const getCurrentUserBuildings = async (setCurrentUserBuildings) => {
  const token = localStorage.getItem("user"); // get token from local storage
  // const user_id = localStorage.getItem("user_id"); // get id from local storage
  try {
    const res = await axios.get("/buildings/current", {
      headers: {
        Authorization: "Bearer " + token,
      },
    });
    setCurrentUserBuildings(res.data);

    console.log("respone:", res.data);
  } catch (error) {
    console.warn("[getInterventions] error:", error);
  }
};

function InternventionsForm() {
  const [buildings, setCurrentUserBuildings] = useState([]);
  const [building_id, setCurrentUserBuildingId] = useState([]);

  let navigate = useNavigate();

  //fetch data  on page load
  useEffect(() => {
    async function fetchData() {
      console.log("Form mounted!");
      await getCurrentUserBuildings(setCurrentUserBuildings);
    }
    fetchData();
  }, []);

  // map on building and save building id
  async function test() {
    const buildingsList = buildings.map((building) => {
      setCurrentUserBuildingId(building.id);
      console.log("test: ", building.id);
      return <option>{building.address}</option>;
    });
  }
  test();
  // useEffect(() => {
  //   const buildingsList = buildings.map((building) => {
  //     setCurrentUserBuildingId(building.id);
  //     return <option>{building.address}</option>;
  //   });
  // }, []);

  // console.log("building id state: ", building_id);
  return (
    <div className="flex-column">
      <div className="flex-row align-self-end">
        <Button
          className="align-self-end m-3"
          variant="primary"
          onClick={() => go_to_home(navigate)}
        >
          Return Back
        </Button>
        <Button
          className="align-self-end m-3"
          variant="danger"
          onClick={() => go_to_authentication(navigate)}
        >
          Log out
        </Button>
      </div>
      <div className="top-section"></div>
      <h1>Request for intervention </h1>
      <div className="m-3">
        <Form.Select aria-label="Default select example">
          <option>Select a building</option>
          {/* {buildingsList} */}
        </Form.Select>
      </div>
    </div>
  );
}

export default InternventionsForm;
